const obj = document.createElement('div');
document.body.appendChild(obj);
obj.style.width = '600px';
//obj.style.height = '300px';
obj.style.background = 'hsl(0, 0%, 10%)';
obj.style.borderRadius = '10px';
obj.style.position = 'absolute';
obj.style.textAlign = 'center';
obj.style.color = 'white';
obj.style.overflow = 'hidden';
obj.style.top = '50%';
obj.style.left = '50%';
obj.style.transform = 'translateX(-50%) translateY(-50%)'

const title2 = document.createElement('div');
title2.innerText = "input row number";
obj.appendChild(title2);

const nrows = obj.appendChild(document.createElement('input'))
let rown = 0

const frame = obj.appendChild(document.createElement('button'));
frame.innerText = "create frame"
frame.onclick = () => {
  rown = nrows.value;
  contnr.appendChild(document.createElement('br'));
  contnr.appendChild(document.createElement('br'));
  contnr.innerHTML = "";
  let celns = []
  for (let i = 0; i < 2; i++) {
   celns.push("hc"+i)
  }
  for (let i = 0; i < celns.length; i++) {
    let button = document.createElement('input');
    button.id=celns[i]
    button.innerText = celns[i];
    contnr.appendChild(button);
    if (celns[i].substring(2,3)==="1") {
      contnr.appendChild(document.createElement('br'));
    }
  }
  let liner = contnr.appendChild(document.createTextNode("---------------------------------------------------------------------------")); 
  contnr.appendChild(document.createElement('br'));
  celns=[]
  for (let i = 0; i < rown; i++) {
   celns.push("r"+i+"c1")
   celns.push("r"+i+"c2")
  }
  for (let i = 0; i < celns.length; i++) {
    let button = document.createElement('input');
    button.id=celns[i]
    button.innerText = celns[i];
    contnr.appendChild(button);
    if (celns[i].substring(3,4)==="2") {
      contnr.appendChild(document.createElement('br'));
    }
  }
}

